// Read the 4 KB section files from disk
const fs = require('fs');
const path = require('path');

const docsPath = 'C:/Users/alex/OneDrive/Desktop/Cursor/equity-connect/docs/REVERSE_MORTGAGE_VECTOR_DATABASE';

const files = [
  'reverse_mortgage_kb_section_1_UPDATED.md',
  'reverse_mortgage_kb_section_2_UPDATED.md',
  'reverse_mortgage_kb_section_3_UPDATED.md',
  'reverse_mortgage_kb_section_4_UPDATED.md'
];

const documents = [];

for (const file of files) {
  try {
    const filePath = path.join(docsPath, file);
    const content = fs.readFileSync(filePath, 'utf8');
    const sectionNum = file.match(/section_(\d+)/)[1];

    // Split by ### headers to create semantic chunks
    const chunks = content.split(/(?=###\s)/);

    chunks.forEach((chunk, index) => {
      const cleanChunk = chunk.trim();

      // Only chunks with substantial content
      if (cleanChunk.length > 100) {
        const headingMatch = cleanChunk.match(/###\s+(.+?)(?:\n|$)/);
        const heading = headingMatch ? headingMatch[1].trim() : `Part ${index + 1}`;

        documents.push({
          json: {
            pageContent: cleanChunk,
            metadata: {
              section: `section_${sectionNum}`,
              section_title: file.replace('_UPDATED.md', ''),
              chunk_heading: heading,
              chunk_index: index,
              content_type: 'reverse_mortgage_kb',
              source: 'barbara_knowledge_base',
              created_at: new Date().toISOString()
            }
          }
        });
      }
    });

    console.log(`✅ Processed ${file}: ${chunks.length} chunks`);

  } catch (error) {
    console.error(`❌ Error reading ${file}:`, error.message);
  }
}

console.log(`\n📊 Total chunks prepared: ${documents.length}`);

return documents;

