// BULK ASSIGN - Process ALL leads at once (no loop!)
// Assigns campaigns based on equity percentage (scalable for any market)
const allLeads = $input.all();

if (allLeads.length === 0) {
  console.log('No fresh leads to process');
  return [];
}

console.log(`Processing ${allLeads.length} fresh leads in bulk...`);

const processedLeads = allLeads.map(item => {
  const lead = item.json;

  const equityAmount = lead.estimated_equity || 0;
  const propertyValue = lead.property_value || 0;
  const history = lead.campaign_history || [];

  // Skip if already has campaign history (shouldn't happen with view filter)
  if (history.length > 0) {
    return null;
  }

  // Calculate equity percentage (scalable for any market)
  const equityPercent = propertyValue > 0 ? (equityAmount / propertyValue) * 100 : 0;

  // Assign campaign based on equity percentage
  let assignedArchetype = '';
  if (equityPercent >= 80) {
    assignedArchetype = 'high_equity_special';
  } else if (equityPercent >= 50) {
    assignedArchetype = 'cash_unlocked';
  } else {
    assignedArchetype = 'no_more_payments';
  }

  // Calculate merge fields
  const equity50 = Math.round(equityAmount * 0.50);
  const equity60 = Math.round(equityAmount * 0.60);
  const equityShort = `$${Math.round(equityAmount / 1000)}K`;
  const estimatedPayment = Math.round((propertyValue * 0.4) * 0.005);
  const valueLow = Math.round(propertyValue * 0.9 / 1000) * 1000;
  const valueHigh = Math.round(propertyValue * 1.1 / 1000) * 1000;
  const valueRange = `$${(valueLow/1000).toFixed(0)}K-$${(valueHigh/1000).toFixed(0)}K`;

  return {
    ...lead,
    assigned_archetype: assignedArchetype,
    equity_percentage: Math.round(equityPercent),
    equity_50_amount: equity50,
    equity_60_amount: equity60,
    equity_short: equityShort,
    estimated_monthly_payment: estimatedPayment,
    property_value_range: valueRange,
    entry_timestamp: new Date().toISOString()
  };
}).filter(Boolean);

console.log(`âœ… Assigned campaigns to ${processedLeads.length} leads`);
console.log(`   High Equity (80%+): ${processedLeads.filter(l => l.assigned_archetype === 'high_equity_special').length}`);
console.log(`   Cash Unlocked (50-79%): ${processedLeads.filter(l => l.assigned_archetype === 'cash_unlocked').length}`);
console.log(`   No More Payments (<50%): ${processedLeads.filter(l => l.assigned_archetype === 'no_more_payments').length}`);

return processedLeads.map(lead => ({ json: lead }));

