// AI Sentiment Analysis using simple keyword detection
// TODO: Replace with actual OpenAI/Claude API call for production
const replyData = $('Extract Reply Data').first().json;
const replyText = (replyData.reply_text || '').toLowerCase();

// Positive keywords
const positiveKeywords = [
  'yes', 'interested', 'info', 'more', 'details', 'tell me',
  'send', 'calculate', 'numbers', 'call', 'schedule', 'meeting',
  'sounds good', 'learn more', 'want to know', 'curious'
];

// Negative keywords
const negativeKeywords = [
  'stop', 'unsubscribe', 'remove', 'not interested', 'no thanks',
  'dont contact', "don't contact", 'leave me alone', 'no',
  'never', 'delete', 'opt out', 'spam'
];

// Check for matches
let sentiment = 'NEUTRAL';
let confidence = 0;

for (const keyword of positiveKeywords) {
  if (replyText.includes(keyword)) {
    sentiment = 'POSITIVE';
    confidence = 80;
    break;
  }
}

for (const keyword of negativeKeywords) {
  if (replyText.includes(keyword)) {
    sentiment = 'NEGATIVE';
    confidence = 95;
    break;
  }
}

// If very short reply (1-3 words) without clear keywords
if (replyText.split(' ').length <= 3 && sentiment === 'NEUTRAL') {
  confidence = 30;
}

console.log(`Sentiment: ${sentiment} (${confidence}% confidence)`);
console.log(`Reply text: "${replyText.substring(0, 100)}..."`);

return [{
  json: {
    ...replyData,
    sentiment: sentiment,
    confidence: confidence,
    analyzed_at: new Date().toISOString()
  }
}];

