const output = $input.first().json;
const context = $('Prepare Broker Context').first().json;

// Extract total token usage from intermediateSteps
let totalInputTokens = 0;
let totalOutputTokens = 0;

if (output.intermediateSteps && Array.isArray(output.intermediateSteps)) {
  output.intermediateSteps.forEach(step => {
    const messageLog = step.action?.messageLog;
    if (messageLog && Array.isArray(messageLog)) {
      messageLog.forEach(msg => {
        if (msg.kwargs?.usage_metadata) {
          const usage = msg.kwargs.usage_metadata;
          totalInputTokens += usage.input_tokens || 0;
          totalOutputTokens += usage.output_tokens || 0;
        }
      });
    }
  });
}

const tokenUsage = totalInputTokens + totalOutputTokens;
// Gemini 2.5 Flash pricing: $0.30/M input, $2.50/M output  
const cost = (totalInputTokens * 0.0000003) + (totalOutputTokens * 0.0000025);

// Count steps
const stepsExecuted = output.intermediateSteps?.length || 0;

// Parse output text for metrics
const outputText = output.output || '';
let leadsUploaded = 0;
let leadsPulled = 0;
let leadsEnriched = 0;
let endingSurplus = 0;

// Extract metrics from AI output
const uploadedMatch = outputText.match(/uploaded.*?(\d+)/i);
if (uploadedMatch) leadsUploaded = parseInt(uploadedMatch[1]);

const pulledMatch = outputText.match(/pulled.*?(\d+)/i) || outputText.match(/(\d+).*?pulled/i);
if (pulledMatch) leadsPulled = parseInt(pulledMatch[1]);

const enrichedMatch = outputText.match(/enriched.*?(\d+)/i) || outputText.match(/(\d+).*?enriched/i);
if (enrichedMatch) leadsEnriched = parseInt(enrichedMatch[1]);

const surplusMatch = outputText.match(/surplus.*?(\d+)/i);
if (surplusMatch) endingSurplus = parseInt(surplusMatch[1]);

// Get starting surplus from context
const startingSurplus = context.daily_lead_surplus || 0;

// Calculate run number for the day (simplified - use execution time)
const executionTime = new Date().toLocaleTimeString('en-US', { 
  timeZone: 'America/Los_Angeles',
  hour: '2-digit', 
  minute: '2-digit',
  hour12: false
});

// Check for errors
const hasError = output.error || outputText.toLowerCase().includes('failed');
const errorMessage = hasError ? (output.error || 'Check workflow output') : null;

return [{
  json: {
    success: !hasError,
    error: hasError,
    error_message: errorMessage,
    broker_id: context.broker_id,
    broker_name: context.broker_name,
    target: context.daily_capacity,
    starting_surplus: startingSurplus,
